import React, { Component } from 'react';
import './main.css';

import axios from 'axios';
// import cookie from 'react-cookies'

class view extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data : [],
      date : "",
    }
  }

  componentWillMount() {
    const board_id = this.props.match.params.data;

    this._getData(board_id);
    this._addViewCnt(board_id);
  }

  _getData = async function(board_id) {
    const getData = await axios('/board/get/board_data', {
      method : 'POST',
      headers: new Headers(),
      data : { id : board_id }
    });

    // 날짜 구하기
    const date = getData.data[0].date.String(slice(0, 10)) + ' ' + getData.data[0].date.String(slice(11, 16));

    return this.setState({ data : getData, date : date })
  }

  _addViewCnt = async function(board_id) {
    const addView = await axios('/board/update/view_cnt', {
      method : 'POST',
      headers: new Headers(),
      data : { id : board_id }
    })
  }

  render() {
    const { data, date } = this.state;

    return (
        <div className='Write'>
          {data.data 
          ? <div>

              <div className='top_title'>
                <input type='text' id='title_txt' name='title' defaultValue={data.data[0].title} readOnly/>

                <div className='date_div'>
                  {date}
                </div>
              </div>
              
              <div>
                <textarea id='content_txt' name='contents' defaultValue={data.data[0].contents} readOnly></textarea>
              </div>
            </div>
          : null}
        </div>
    );
  }
}

export default view;
